import React, { useState } from 'react';
import axios from 'axios';

function IncidentList({ incidents, onIncidentUpdate }) {
  if (incidents.length === 0) {
    return (
      <div className="no-incidents">
        <p>‚úÖ No tourist warnings today - All areas safe!</p>
      </div>
    );
  }

  return (
    <div className="incident-list">
      {incidents.map(incident => (
        <div key={incident._id} className={`incident-card ${incident.resolved ? 'resolved' : 'active'}`}>
          <div className="incident-header">
            <span className="beacon-id">{incident.beaconId}</span>
            <span className="timestamp">{new Date(incident.timestamp).toLocaleString()}</span>
          </div>

          <div className="incident-details">
            <p>üìç Location: {incident.latitude}, {incident.longitude}</p>
            <p>üîó Blockchain: {incident.blockchainTxHash}</p>
            <p>Status: {incident.resolved ? '‚úÖ Resolved' : 'üö® Tourist Warned'}</p>
          </div>

          <div className="incident-actions">
            {!incident.resolved && (
              <button 
                className="resolve-btn"
                onClick={() => handleResolve(incident._id)}
              >
                Mark as Resolved
              </button>
            )}
            <button 
              className="view-btn"
              onClick={() => handleViewDetails(incident)}
            >
              View Details
            </button>
          </div>
        </div>
      ))}
    </div>
  );
}

export default IncidentList;
