import React from 'react';
import { MapContainer, TileLayer, Marker, Popup, Circle } from 'react-leaflet';
import L from 'leaflet';

// Fix for default markers
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

// Custom icons
const beaconIcon = new L.Icon({
  iconUrl: 'data:image/svg+xml;base64,',
  iconSize: [25, 25],
  iconAnchor: [12, 12]
});

const alertIcon = new L.Icon({
  iconUrl: 'data:image/svg+xml;base64,',
  iconSize: [20, 20],
  iconAnchor: [10, 10]
});

function BeaconMap({ beacons, incidents }) {
  const center = [10.3270, 76.9550]; // Default center

  return (
    <MapContainer center={center} zoom={15} style={{ height: '400px', width: '100%' }}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'
      />

      {/* Beacon markers */}
      {beacons.map(beacon => (
        <Marker key={beacon.id} position={[beacon.lat, beacon.lng]} icon={beaconIcon}>
          <Popup>
            <div>
              <h3>{beacon.name}</h3>
              <p>Beacon ID: {beacon.id}</p>
              <p>Status: <span style={{color: beacon.status === 'active' ? 'green' : 'red'}}>{beacon.status}</span></p>
              <p>Location: {beacon.lat}, {beacon.lng}</p>
            </div>
          </Popup>
          <Circle
            center={[beacon.lat, beacon.lng]}
            radius={50}
            pathOptions={{ color: 'red', fillColor: 'red', fillOpacity: 0.1 }}
          />
        </Marker>
      ))}

      {/* Incident markers */}
      {incidents.map(incident => (
        <Marker 
          key={incident._id} 
          position={[incident.latitude, incident.longitude]} 
          icon={alertIcon}
        >
          <Popup>
            <div>
              <h3>üö® Tourist Warning Triggered</h3>
              <p>Beacon: {incident.beaconId}</p>
              <p>Time: {new Date(incident.timestamp).toLocaleString()}</p>
              <p>Blockchain: {incident.blockchainTxHash}</p>
              <p>Status: {incident.resolved ? '‚úÖ Resolved' : '‚ö†Ô∏è Active'}</p>
            </div>
          </Popup>
        </Marker>
      ))}
    </MapContainer>
  );
}

export default BeaconMap;
